CREATE TABLE IF NOT EXISTS usuarios(email VARCHAR(50) NOT NULL PRIMARY KEY, nombre VARCHAR(50) NOT NULL, password VARCHAR(200), tipo INT NOT NULL DEFAULT 0, inventario INT NOT NULL DEFAULT 0);
CREATE TABLE IF NOT EXISTS inventario(id INT NOT NULL PRIMARY KEY AUTO_INCREMENT, admin VARCHAR(50) FOREIGN KEY REFERENCES usuarios(email), nombre VARCHAR(50) NOT NULL);
CREATE TABLE IF NOT EXISTS productos(id INT NOT NULL, inventario INT NOT NULL, stock INT NOT NULL, valido INT NOT NULL DEFAULT 1, PRIMARY KEY(id, inventario), FOREIGN KEY(inventario) REFERENCES inventario(id));
CREATE TABLE IF NOT EXISTS data_productos(id INT NOT NULL, inventario INT NOT NULL, nombre VARCHAR(50) NOT NULL, descripcion VARCHAR(200), precio INT NOT NULL, PRIMARY KEY(id, inventario), FOREIGN KEY(id, inventario) REFERENCES productos(id, inventario));
CREATE TABLE IF NOT EXISTS despachos(id INT NOT NULL PRIMARY KEY AUTO_INCREMENT, inventario INT NOT NULL FOREIGN KEY REFERENCES inventario(id), productos INT ARRAY NOT NULL, cantidad INT ARRAY NOT NULL, direccion VARCHAR(200) NOT NULL, responsable VARCHAR(50) NOT NULL FOREIGN KEY REFERENCES usuarios(email), comprador VARCHAR(50) NOT NULL);
